var taskMap map[int]int // key num value count

func minimumRounds(tasks []int) int {
	if len(tasks) == 1 {
		return -1
	}
	if len(tasks) == 2 {
		if tasks[0] != tasks[1] {
			return -1
		}
	}
	maxLen := 0
	taskMap = make(map[int]int)
	for i := 0; i < len(tasks); i++ {
		if c, ok := taskMap[tasks[i]]; ok {
			taskMap[tasks[i]] = c + 1
			maxLen = getMax(maxLen, c+1)
		} else {
			maxLen = getMax(maxLen, 1)
			taskMap[tasks[i]] = 1
		}
	}
	dp2244 := make([]int, maxLen+1)
	dp2244[0] = 0
	dp2244[1] = 0
	for k := 2; k < maxLen+1; k++ {
		if k == 2 || k == 3 {
			dp2244[k] = 1
		} else if k == 4 {
			dp2244[k] = 2
		} else {
			dp2244[k] = getMin(dp2244[k-2], dp2244[k-3]) + 1
		}
	}

	var res int
	for _, count := range taskMap {
		if count == 1 {
			return -1
		}
		res += dp2244[count]
	}
	return res
}

